mapper.select:

		SELECT *
		  FROM calendar C
		    INNER JOIN food F
		      ON C.fCode = F.fCode
    	  WHERE id=#{condId} 
		  	AND eYear=#{condYear} 
		  	AND eMonth=#{condMonth} 
		  	AND eDate=#{condDate}


		SELECT *
		  FROM calendar C
		    INNER JOIN food F
		      ON C.fCode = F.fCode
    	  WHERE id=#{condId} 
		  	AND eYear=#{condYear} 
		  	AND eMonth=#{condMonth} 
		  	AND eDate=#{condDate}
		  	AND eTime=#{condTime}

mapper.insert:
INSERT INTO food (
	fCode, fName, f_category, servings, kcal, kcal_per_100
) VALUES (
	#{fCode}, #{fName}, #{f_category}, #{servings}, #{kcal}, #{kcal_per_100} 
)

INSERT INTO calendar (
	id, fCode, eYear, eMonth, eDate, eAmt, eTime
) VALUES (
	#{id}, #{fCode}, #{eYear}, #{eMonth}, #{eDate}, #{eAmt}, #{eTime}
)
